SET NAMES utf8;
SET time_zone = '+00:00';
SET foreign_key_checks = 0;
SET sql_mode = 'NO_AUTO_VALUE_ON_ZERO';

DROP TABLE IF EXISTS `__PREFIX__cache`;
CREATE TABLE `__PREFIX__cache` (
  `cache_id` varchar(255) NOT NULL,
  `cache_value` mediumtext NOT NULL,
  `cache_manual` enum('0','1') NOT NULL DEFAULT '0',
  PRIMARY KEY (`cache_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='qEngine Cache';


DROP TABLE IF EXISTS `__PREFIX__config`;
CREATE TABLE `__PREFIX__config` (
  `idx` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `group_id` varchar(255) NOT NULL,
  `config_id` varchar(255) NOT NULL DEFAULT '',
  `config_value` varchar(255) NOT NULL,
  PRIMARY KEY (`idx`),
  KEY `config_id` (`config_id`),
  KEY `group_id` (`group_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='qEngine Configuration';



DROP TABLE IF EXISTS `__PREFIX__ip_log`;
CREATE TABLE `__PREFIX__ip_log` (
  `idx` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `log_time` int(10) unsigned NOT NULL DEFAULT '0',
  `log_ip_addr` varchar(45) NOT NULL DEFAULT '',
  `log_user_id` varchar(80) NOT NULL DEFAULT '',
  `log_user_type` char(1) NOT NULL DEFAULT 'U',
  `log_success` char(1) NOT NULL DEFAULT '0',
  PRIMARY KEY (`idx`),
  KEY `log_user_type` (`log_user_type`),
  KEY `log_ip_addr` (`log_ip_addr`),
  KEY `log_user_id` (`log_user_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `__PREFIX__language`;
CREATE TABLE `__PREFIX__language` (
  `lang_id` char(2) NOT NULL,
  `lang_key` varchar(255) NOT NULL,
  `lang_value` text NOT NULL,
  KEY `lang_id` (`lang_id`),
  KEY `lang_key` (`lang_key`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;



DROP TABLE IF EXISTS `__PREFIX__mailog`;
CREATE TABLE `__PREFIX__mailog` (
  `log_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `log_address` varchar(255) NOT NULL DEFAULT '',
  `log_subject` varchar(255) NOT NULL DEFAULT '',
  `log_body` text NOT NULL,
  `log_time` int(10) unsigned NOT NULL DEFAULT '0',
  `log_html` char(1) NOT NULL DEFAULT '',
  PRIMARY KEY (`log_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `__PREFIX__menu_item`;
CREATE TABLE `__PREFIX__menu_item` (
  `idx` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `menu_id` int(10) unsigned NOT NULL,
  `menu_parent` int(10) unsigned NOT NULL,
  `menu_item` varchar(255) NOT NULL,
  `menu_url` varchar(255) NOT NULL,
  `menu_permalink` varchar(255) NOT NULL,
  `menu_order` int(10) unsigned NOT NULL,
  `ref_idx` int(10) unsigned NOT NULL,
  PRIMARY KEY (`idx`),
  KEY `menu_id` (`menu_id`),
  KEY `ref_idx` (`ref_idx`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `__PREFIX__menu_set`;
CREATE TABLE `__PREFIX__menu_set` (
  `idx` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `menu_id` varchar(255) NOT NULL,
  `menu_title` varchar(255) NOT NULL,
  `menu_preset` varchar(255) NOT NULL,
  `menu_class` varchar(255) NOT NULL,
  `menu_notes` text NOT NULL,
  `menu_cache` text NOT NULL,
  `menu_locked` enum('0','1') NOT NULL DEFAULT '0',
  `max_depth` tinyint(3) unsigned NOT NULL DEFAULT '1',
  `ref_idx` int(10) unsigned NOT NULL,
  PRIMARY KEY (`idx`),
  KEY `ref_idx` (`ref_idx`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `__PREFIX__module`;
CREATE TABLE `__PREFIX__module` (
  `mod_id` varchar(255) NOT NULL DEFAULT '',
  `mod_type` enum('general','shipping','payment') NOT NULL DEFAULT 'general',
  `mod_name` varchar(255) NOT NULL DEFAULT '',
  `mod_desc` text NOT NULL,
  `mod_version` varchar(10) NOT NULL DEFAULT '',
  `mod_css` varchar(255) NOT NULL,
  `mod_js` varchar(255) NOT NULL,
  `mod_enabled` char(1) NOT NULL DEFAULT '',
  PRIMARY KEY (`mod_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `__PREFIX__module_pos`;
CREATE TABLE `__PREFIX__module_pos` (
  `idx` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `mod_id` varchar(255) NOT NULL,
  `mod_title` varchar(255) NOT NULL,
  `mod_config` text NOT NULL,
  `mod_pos` char(2) NOT NULL,
  PRIMARY KEY (`idx`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `__PREFIX__notification`;
CREATE TABLE `__PREFIX__notification` (
  `idx` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `notify_time` int(10) unsigned NOT NULL,
  `notify_from` varchar(255) NOT NULL,
  `notify_to` varchar(255) NOT NULL,
  `notify_admin` enum('0','1') NOT NULL DEFAULT '0',
  `notify_subject` varchar(255) NOT NULL,
  `notify_url` varchar(255) NOT NULL,
  `notify_read` enum('0','1') NOT NULL DEFAULT '0',
  `notify_popup` enum('0','1') NOT NULL DEFAULT '0',
  PRIMARY KEY (`idx`),
  KEY `notify_to` (`notify_to`),
  KEY `notify_admin` (`notify_admin`),
  KEY `notify_popup` (`notify_popup`),
  KEY `notify_from` (`notify_from`),
  KEY `notify_read` (`notify_read`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `__PREFIX__page`;
CREATE TABLE `__PREFIX__page` (
  `group_id` varchar(5) NOT NULL,
  `cat_id` int(10) unsigned NOT NULL,
  `page_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `permalink` varchar(255) NOT NULL,
  `page_image` varchar(255) NOT NULL DEFAULT '',
  `page_date` date NOT NULL DEFAULT '0000-00-00',
  `page_time` time NOT NULL,
  `page_unix` int(10) unsigned NOT NULL,
  `page_title` varchar(255) NOT NULL DEFAULT '',
  `page_keyword` varchar(255) NOT NULL,
  `page_body` text NOT NULL,
  `page_author` varchar(255) NOT NULL,
  `page_related` varchar(255) NOT NULL,
  `page_allow_comment` char(1) NOT NULL DEFAULT '0',
  `last_update` int(10) unsigned NOT NULL,
  `page_rating` decimal(3,2) unsigned NOT NULL,
  `page_comment` int(10) unsigned NOT NULL,
  `page_list` char(1) NOT NULL,
  `page_hit` int(10) unsigned NOT NULL,
  `page_img_tmp` varchar(255) NOT NULL DEFAULT '',
  `page_attachment` varchar(255) NOT NULL DEFAULT '',
  `page_attachment_stat` int(10) unsigned NOT NULL,
  `page_download` varchar(15) NOT NULL,
  `page_pinned` char(1) NOT NULL DEFAULT '0',
  `page_status` enum('D','P','M','A','H') NOT NULL DEFAULT 'D',
  `page_template` varchar(255) NOT NULL,
  `page_mode` enum('raw','html') NOT NULL DEFAULT 'html',
  PRIMARY KEY (`page_id`),
  KEY `group_id` (`group_id`),
  KEY `cat_id` (`cat_id`),
  KEY `page_author` (`page_author`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `__PREFIX__page_cat`;
CREATE TABLE `__PREFIX__page_cat` (
  `idx` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `group_id` varchar(5) NOT NULL,
  `parent_id` int(10) unsigned NOT NULL,
  `permalink` varchar(255) NOT NULL,
  `cat_name` varchar(255) NOT NULL,
  `cat_details` text NOT NULL,
  `cat_image` varchar(255) NOT NULL,
  PRIMARY KEY (`idx`),
  KEY `group_id` (`group_id`),
  KEY `parent_id` (`parent_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `__PREFIX__page_group`;
CREATE TABLE `__PREFIX__page_group` (
  `idx` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `group_id` varchar(5) NOT NULL,
  `group_title` varchar(255) NOT NULL,
  `group_notes` varchar(255) NOT NULL,
  `all_cat_list` char(1) NOT NULL,
  `cat_list` char(1) NOT NULL,
  `page_cat` char(1) NOT NULL,
  `page_image` char(1) NOT NULL,
  `page_image_size` int(10) unsigned NOT NULL,
  `page_thumb` int(10) unsigned NOT NULL,
  `page_gallery` char(1) NOT NULL,
  `page_gallery_thumb` int(10) unsigned NOT NULL,
  `page_author` char(1) NOT NULL,
  `page_comment` varchar(15) NOT NULL,
  `page_attachment` char(1) NOT NULL,
  `page_download` varchar(15) NOT NULL,
  `page_date` char(1) NOT NULL,
  `page_folder` varchar(25) NOT NULL,
  `page_sort` enum('t','d') NOT NULL DEFAULT 't',
  `hidden_private` char(1) NOT NULL,
  `group_template` varchar(255) NOT NULL,
  `page_template` varchar(255) NOT NULL,
  PRIMARY KEY (`idx`),
  KEY `group_id` (`group_id`),
  KEY `hidden_private` (`hidden_private`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;



DROP TABLE IF EXISTS `__PREFIX__permalink`;
CREATE TABLE `__PREFIX__permalink` (
  `idx` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `url` varchar(255) NOT NULL,
  `target_script` varchar(255) NOT NULL,
  `target_idx` varchar(255) NOT NULL,
  `target_param` varchar(255) NOT NULL,
  PRIMARY KEY (`idx`),
  KEY `url` (`url`),
  KEY `target_script` (`target_script`),
  KEY `target_idx` (`target_idx`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `__PREFIX__qadmin_log`;
CREATE TABLE `__PREFIX__qadmin_log` (
  `log_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `log_date` int(10) unsigned NOT NULL,
  `log_file` varchar(255) NOT NULL,
  `log_table` varchar(255) NOT NULL,
  `log_user` varchar(255) NOT NULL,
  `log_ip` varchar(45) NOT NULL,
  `log_pid` varchar(255) NOT NULL,
  `log_title` varchar(255) NOT NULL,
  `log_action` int(10) unsigned NOT NULL,
  `log_previous` text NOT NULL,
  `log_now` text NOT NULL,
  PRIMARY KEY (`log_id`),
  KEY `log_ip` (`log_ip`),
  KEY `log_user` (`log_user`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `__PREFIX__qcomment`;
CREATE TABLE `__PREFIX__qcomment` (
  `comment_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `mod_id` varchar(255) NOT NULL DEFAULT '',
  `item_id` varchar(80) NOT NULL,
  `item_title` varchar(255) NOT NULL,
  `item_url` varchar(255) NOT NULL,
  `comment_user` varchar(255) NOT NULL DEFAULT '',
  `comment_title` varchar(255) NOT NULL DEFAULT '',
  `comment_body` text NOT NULL,
  `comment_date` date NOT NULL DEFAULT '0000-00-00',
  `comment_rate` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `comment_helpful` varchar(16) NOT NULL,
  `comment_approve` char(1) NOT NULL DEFAULT '',
  PRIMARY KEY (`comment_id`),
  KEY `mod_id` (`mod_id`),
  KEY `item_id` (`item_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='No comment!';


DROP TABLE IF EXISTS `__PREFIX__qcomment_set`;
CREATE TABLE `__PREFIX__qcomment_set` (
  `group_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `comment_mode` char(1) NOT NULL,
  `comment_approval` char(1) NOT NULL,
  `member_only` char(1) NOT NULL,
  `unique_comment` char(1) NOT NULL,
  `comment_helpful` char(1) NOT NULL,
  `comment_on_comment` char(1) NOT NULL,
  `captcha` char(1) NOT NULL,
  `detail` char(1) NOT NULL,
  `mod_id` varchar(15) NOT NULL,
  `notes` varchar(255) NOT NULL,
  PRIMARY KEY (`group_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `__PREFIX__stats_daily`;
CREATE TABLE `__PREFIX__stats_daily` (
  `stats_date` date NOT NULL,
  `stats_hit` int(10) unsigned NOT NULL,
  `stats_visit` int(10) unsigned NOT NULL,
  PRIMARY KEY (`stats_date`),
  KEY `stats_hit` (`stats_hit`),
  KEY `stats_visit` (`stats_visit`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `__PREFIX__stats_ip`;
CREATE TABLE `__PREFIX__stats_ip` (
  `ip_address` varchar(45) NOT NULL,
  `last_update` int(10) unsigned NOT NULL,
  PRIMARY KEY (`ip_address`),
  KEY `last_update` (`last_update`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `__PREFIX__user`;
CREATE TABLE `__PREFIX__user` (
  `user_id` varchar(80) NOT NULL DEFAULT '',
  `user_passwd` varchar(255) NOT NULL DEFAULT '',
  `user_email` varchar(255) NOT NULL DEFAULT '',
  `user_level` char(1) NOT NULL DEFAULT '1',
  `user_since` date NOT NULL DEFAULT '0000-00-00',
  `admin_level` char(1) NOT NULL DEFAULT '0',
  `reset_passwd` varchar(16) NOT NULL DEFAULT '',
  `user_activation` varchar(16) NOT NULL DEFAULT '',
  `axsrf_token` varchar(32) NOT NULL,
  `user_notes` text NOT NULL,
  PRIMARY KEY (`user_id`),
  KEY `user_passwd` (`user_passwd`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='Member profile & billing address';